import React, { useState, useEffect } from 'react';
import { Wallet, TrendingUp, LogOut, ArrowRight, CheckCircle, ExternalLink, AlertCircle } from 'lucide-react';
import { ethers } from 'ethers';
import CoinbaseWalletSDK from '@coinbase/wallet-sdk';

// ⚠️ UPDATE THIS AFTER DEPLOYING THE CONTRACT
const CONTRACT_ADDRESS = '0x350386d7FB4d9F230ce52eEE794ddc5392777048';
const BASE_CHAIN_ID = 8453;
const BASE_CHAIN_ID_HEX = '0x2105';

const CONTRACT_ABI = [
  "function deposit(uint256 usdcAmount) external",
  "function withdraw() external",
  "function getUserBalances(address user) external view returns (uint256, uint256, uint256, uint256, uint256)",
  "event Deposited(address indexed user, uint256 usdcAmount)",
  "event Withdrawn(address indexed user, uint256 usdcAmount)"
];

const USDC_ABI = [
  "function approve(address spender, uint256 amount) external returns (bool)",
  "function allowance(address owner, address spender) external view returns (uint256)",
  "function balanceOf(address account) external view returns (uint256)",
  "function decimals() external view returns (uint8)"
];

const USDC_ADDRESS = '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913';

// Token configuration: WETH, SOL, cbXRP, cbLTC, cbADA
const ASSETS = [
  { name: 'Ethereum', symbol: 'WETH', decimals: 18, color: 'from-blue-400 to-blue-600' },
  { name: 'Solana', symbol: 'SOL', decimals: 9, color: 'from-purple-400 to-fuchsia-500' },
  { name: 'XRP', symbol: 'cbXRP', decimals: 18, color: 'from-slate-400 to-slate-600' },
  { name: 'Litecoin', symbol: 'cbLTC', decimals: 8, color: 'from-gray-400 to-gray-600' },
  { name: 'Cardano', symbol: 'cbADA', decimals: 18, color: 'from-sky-400 to-blue-600' }
];

function App() {
  const [sdk, setSdk] = useState(null);
  const [provider, setProvider] = useState(null);
  const [account, setAccount] = useState(null);
  const [signer, setSigner] = useState(null);
  const [activeTab, setActiveTab] = useState('deposit');
  const [usdcAmount, setUsdcAmount] = useState('');
  const [balances, setBalances] = useState([0, 0, 0, 0, 0]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isApproved, setIsApproved] = useState(false);
  const [usdcBalance, setUsdcBalance] = useState('0');

  useEffect(() => {
    const coinbaseWallet = new CoinbaseWalletSDK({
      appName: 'TopL1',
      darkMode: true
    });

    const walletProvider = coinbaseWallet.makeWeb3Provider();

    setSdk(coinbaseWallet);
    setProvider(walletProvider);

    walletProvider.on('accountsChanged', (accounts) => {
      if (accounts.length === 0) {
        disconnectWallet();
      } else {
        setAccount(accounts[0]);
      }
    });

    walletProvider.on('chainChanged', (chainId) => {
      if (parseInt(chainId) !== BASE_CHAIN_ID) {
        setError('Please switch to Base network');
      } else {
        setError('');
      }
    });

    return () => {
      if (walletProvider) {
        walletProvider.removeAllListeners();
      }
    };
  }, []);

  useEffect(() => {
    if (account && signer) {
      loadBalances();
      checkApproval();
      loadUSDCBalance();
    }
  }, [account, signer]);

  const connectWallet = async () => {
    try {
      setError('');
      setLoading(true);

      if (!provider) {
        setError('Wallet provider not initialized');
        return;
      }

      const accounts = await provider.request({
        method: 'eth_requestAccounts'
      });

      if (!accounts || accounts.length === 0) {
        setError('No accounts found. Please unlock your Coinbase Wallet.');
        return;
      }

      const chainId = await provider.request({ method: 'eth_chainId' });

      if (chainId !== BASE_CHAIN_ID_HEX) {
        try {
          await provider.request({
            method: 'wallet_switchEthereumChain',
            params: [{ chainId: BASE_CHAIN_ID_HEX }],
          });
        } catch (switchError) {
          if (switchError.code === 4902) {
            await provider.request({
              method: 'wallet_addEthereumChain',
              params: [{
                chainId: BASE_CHAIN_ID_HEX,
                chainName: 'Base',
                nativeCurrency: {
                  name: 'Ethereum',
                  symbol: 'ETH',
                  decimals: 18
                },
                rpcUrls: ['https://mainnet.base.org'],
                blockExplorerUrls: ['https://basescan.org']
              }]
            });
          } else {
            throw switchError;
          }
        }
      }

      const ethersProvider = new ethers.BrowserProvider(provider);
      const ethersSigner = await ethersProvider.getSigner();

      setAccount(accounts[0]);
      setSigner(ethersSigner);
      setSuccess('Wallet connected successfully!');
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Connection error:', err);
      if (err.code === 4001) {
        setError('Connection rejected by user');
      } else {
        setError('Failed to connect: ' + (err.message || 'Unknown error'));
      }
    } finally {
      setLoading(false);
    }
  };

  const disconnectWallet = () => {
    if (provider) {
      provider.disconnect();
    }
    setAccount(null);
    setSigner(null);
    setBalances([0, 0, 0, 0, 0]);
    setIsApproved(false);
    setUsdcAmount('');
    setUsdcBalance('0');
    setSuccess('Wallet disconnected');
    setTimeout(() => setSuccess(''), 3000);
  };

  const loadUSDCBalance = async () => {
    if (!signer || !account) return;

    try {
      const usdcContract = new ethers.Contract(USDC_ADDRESS, USDC_ABI, signer);
      const balance = await usdcContract.balanceOf(account);
      setUsdcBalance(ethers.formatUnits(balance, 6));
    } catch (err) {
      console.error('Error loading USDC balance:', err);
    }
  };

  const checkApproval = async () => {
    if (!signer || !account) return;

    try {
      const usdcContract = new ethers.Contract(USDC_ADDRESS, USDC_ABI, signer);
      const allowance = await usdcContract.allowance(account, CONTRACT_ADDRESS);
      setIsApproved(allowance > 0);
    } catch (err) {
      console.error('Error checking approval:', err);
    }
  };

  const approveUSDC = async () => {
    if (!signer) return;

    setLoading(true);
    setError('');

    try {
      const usdcContract = new ethers.Contract(USDC_ADDRESS, USDC_ABI, signer);
      const tx = await usdcContract.approve(CONTRACT_ADDRESS, ethers.MaxUint256);
      setSuccess('Approval transaction submitted... Please wait.');
      await tx.wait();
      setIsApproved(true);
      setSuccess('USDC approved successfully!');
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Approval error:', err);
      if (err.code === 4001 || err.code === 'ACTION_REJECTED') {
        setError('Approval rejected by user');
      } else if (err.message.includes('insufficient funds')) {
        setError('Insufficient ETH for gas fees');
      } else {
        setError('Approval failed: ' + (err.shortMessage || err.message || 'Unknown error'));
      }
    } finally {
      setLoading(false);
    }
  };

  const loadBalances = async () => {
    if (!signer || !account) return;

    try {
      const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);
      const result = await contract.getUserBalances(account);

      setBalances([
        Number(ethers.formatUnits(result[0], ASSETS[0].decimals)), // WETH
        Number(ethers.formatUnits(result[1], ASSETS[1].decimals)), // SOL
        Number(ethers.formatUnits(result[2], ASSETS[2].decimals)), // cbXRP
        Number(ethers.formatUnits(result[3], ASSETS[3].decimals)), // cbLTC
        Number(ethers.formatUnits(result[4], ASSETS[4].decimals))  // cbADA
      ]);
    } catch (err) {
      console.error('Error loading balances:', err);
    }
  };

  const handleDeposit = async () => {
    if (!signer || !usdcAmount) return;

    setLoading(true);
    setError('');

    try {
      const amount = ethers.parseUnits(usdcAmount, 6);

      // Validate amount
      if (parseFloat(usdcAmount) < 10) {
        setError('⚠️ Minimum deposit is 10 USDC. Smaller amounts may fail during withdrawal due to DEX liquidity constraints.');
        setLoading(false);
        return;
      }

      // Check USDC balance
      const usdcContract = new ethers.Contract(USDC_ADDRESS, USDC_ABI, signer);
      const balance = await usdcContract.balanceOf(account);

      if (balance < amount) {
        setError(`Insufficient USDC. You have ${ethers.formatUnits(balance, 6)} USDC`);
        setLoading(false);
        return;
      }

      // Check allowance
      const allowance = await usdcContract.allowance(account, CONTRACT_ADDRESS);
      if (allowance < amount) {
        setError('Please approve USDC first');
        setIsApproved(false);
        setLoading(false);
        return;
      }

      const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);

      // Estimate gas
      try {
        await contract.deposit.estimateGas(amount);
      } catch (gasError) {
        console.error('Gas estimation failed:', gasError);
        setError('⚠️ Transaction will likely fail. Reasons: 1) Insufficient liquidity in Aerodrome pools 2) You need more ETH for gas 3) Try a smaller amount');
        setLoading(false);
        return;
      }

      const tx = await contract.deposit(amount);
      setSuccess('Transaction submitted! Swapping and distributing assets...');

      await tx.wait();

      setSuccess('✅ Deposit successful! Assets distributed to 5 L1 tokens.');
      setUsdcAmount('');
      await loadBalances();
      await loadUSDCBalance();
      setTimeout(() => setSuccess(''), 5000);
    } catch (err) {
      console.error('Deposit error:', err);
      if (err.code === 4001 || err.code === 'ACTION_REJECTED') {
        setError('Transaction rejected by user');
      } else if (err.message.includes('insufficient funds')) {
        setError('Insufficient ETH for gas fees. Bridge more ETH to Base.');
      } else if (err.reason) {
        setError('Contract error: ' + err.reason);
      } else {
        setError('Deposit failed: ' + (err.shortMessage || err.message || 'Unknown error'));
      }
    } finally {
      setLoading(false);
    }
  };

  const handleWithdraw = async () => {
    if (!signer) return;

    setLoading(true);
    setError('');

    try {
      const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);

      // Check if user has balances
      const result = await contract.getUserBalances(account);
      const hasBalance = result.some(val => val > 0n);

      if (!hasBalance) {
        setError('No balance to withdraw');
        setLoading(false);
        return;
      }

      // Just log warning but don't block withdrawal
      const wethBalance = Number(ethers.formatUnits(result[0], 18));
      if (wethBalance < 0.001) {
        console.warn('Small withdrawal amount - gas fees may exceed value');
      }

      const tx = await contract.withdraw();
      setSuccess('Withdrawal submitted! Converting all assets back to USDC...');

      await tx.wait();

      setSuccess('✅ Withdrawal successful! USDC returned to your wallet.');
      await loadBalances();
      await loadUSDCBalance();
      setTimeout(() => setSuccess(''), 5000);
    } catch (err) {
      console.error('Withdraw error:', err);
      if (err.code === 4001 || err.code === 'ACTION_REJECTED') {
        setError('Transaction rejected by user');
      } else if (err.message.includes('insufficient funds')) {
        setError('Insufficient ETH for gas fees');
      } else if (err.message.includes('No balance')) {
        setError('No balance to withdraw');
      } else if (err.message.includes('execution reverted')) {
        setError('⚠️ Withdrawal failed on-chain. Likely reasons: 1) Amounts too small to swap (need minimum liquidity) 2) DEX pools lack liquidity. Consider depositing more first.');
      } else {
        setError('Withdrawal failed: ' + (err.shortMessage || err.message || 'Unknown error'));
      }
    } finally {
      setLoading(false);
    }
  };

  const calculateSplit = () => {
    if (!usdcAmount) return [];
    const amount = parseFloat(usdcAmount);
    const portion = amount * 0.2;
    return ASSETS.map(asset => ({ ...asset, amount: portion }));
  };

  const formatBalance = (balance, decimals) => {
    if (decimals === 8 || decimals === 9) {
      return balance.toFixed(8);
    }
    return balance.toFixed(6);
  };

  if (!account) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-slate-800 to-gray-900 flex items-center justify-center p-4 sm:p-6">
        <div className="max-w-md w-full">
          <div className="text-center mb-6 sm:mb-8">
            <h1 className="text-4xl sm:text-5xl font-bold text-white mb-2 sm:mb-3">
              Top<span className="bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent">L1</span>
            </h1>
            <p className="text-gray-400 text-sm sm:text-base">Auto-diversify into Top Layer 1 Assets</p>
            <div className="flex flex-wrap justify-center gap-2 mt-3">
              {ASSETS.map((asset, idx) => (
                <span key={idx} className={`text-xs bg-gradient-to-r ${asset.color} px-2 py-1 rounded-full text-white font-medium`}>
                  {asset.name}
                </span>
              ))}
            </div>
            {CONTRACT_ADDRESS !== '0x0000000000000000000000000000000000000000' && (
              <a 
                href={`https://basescan.org/address/${CONTRACT_ADDRESS}#code`}
                target="_blank" 
                rel="noopener noreferrer"
                className="text-blue-400 text-xs sm:text-sm hover:underline mt-3 inline-flex items-center gap-1"
              >
                View Contract <ExternalLink className="w-3 h-3" />
              </a>
            )}
          </div>

          <div className="backdrop-blur-xl bg-white/5 rounded-2xl sm:rounded-3xl p-6 sm:p-8 border border-white/10">
            <div className="flex justify-center mb-4 sm:mb-6">
              <div className="w-16 h-16 rounded-full bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 flex items-center justify-center">
                <Wallet className="w-8 h-8 text-white" />
              </div>
            </div>

            <h2 className="text-xl sm:text-2xl font-semibold text-white text-center mb-2 sm:mb-3">
              Connect Your Wallet
            </h2>
            <p className="text-gray-400 text-center mb-4 sm:mb-6 text-sm sm:text-base">
              Connect your Coinbase Wallet to get started
            </p>

            <button
              onClick={connectWallet}
              disabled={loading}
              className="w-full bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 text-white font-semibold py-3 sm:py-4 px-6 rounded-xl hover:opacity-90 transition-all duration-200 shadow-lg hover:shadow-xl disabled:opacity-50 text-sm sm:text-base active:scale-95"
            >
              {loading ? 'Connecting...' : 'Connect Coinbase Wallet'}
            </button>

            <p className="text-xs text-gray-500 text-center mt-3 sm:mt-4">
              Make sure MetaMask is disabled if you have it installed
            </p>

            {error && (
              <div className="mt-3 sm:mt-4 bg-red-500/20 border border-red-500/50 text-red-200 px-3 sm:px-4 py-2 sm:py-3 rounded-lg text-xs sm:text-sm flex items-start gap-2">
                <AlertCircle className="w-4 h-4 flex-shrink-0 mt-0.5" />
                <span>{error}</span>
              </div>
            )}
          </div>

          <div className="text-center mt-4 sm:mt-6 text-xs sm:text-sm text-gray-500 px-4">
            Powered by Aerodrome on Base • Immutable Smart Contract
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-slate-800 to-gray-900 p-3 sm:p-4">
      <div className="max-w-4xl mx-auto">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4 sm:mb-8 gap-3 sm:gap-0">
          <div>
            <h1 className="text-3xl sm:text-4xl font-bold text-white">
              Top<span className="bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent">L1</span>
            </h1>
            <p className="text-gray-400 text-xs sm:text-sm mt-1">Auto-diversify into Top Layer 1 Assets</p>
            <p className="text-gray-500 text-xs mt-1">USDC Balance: {parseFloat(usdcBalance).toFixed(2)}</p>
          </div>
          <button
            onClick={disconnectWallet}
            className="flex items-center gap-2 bg-white/10 hover:bg-white/20 text-white px-3 sm:px-4 py-2 rounded-xl transition-all backdrop-blur-sm text-sm active:scale-95"
          >
            <span className="text-xs sm:text-sm">{account.slice(0, 6)}...{account.slice(-4)}</span>
            <LogOut className="w-3 h-3 sm:w-4 sm:h-4" />
          </button>
        </div>

        {(error || success) && (
          <div className={`mb-4 sm:mb-6 ${success ? 'bg-green-500/20 border-green-500/50 text-green-200' : 'bg-red-500/20 border-red-500/50 text-red-200'} border px-3 sm:px-4 py-2 sm:py-3 rounded-lg flex items-start gap-2 text-xs sm:text-sm`}>
            {success ? <CheckCircle className="w-4 h-4 sm:w-5 sm:h-5 flex-shrink-0 mt-0.5" /> : <AlertCircle className="w-4 h-4 sm:w-5 sm:h-5 flex-shrink-0 mt-0.5" />}
            <span>{error || success}</span>
          </div>
        )}

        <div className="backdrop-blur-xl bg-white/5 rounded-2xl sm:rounded-3xl border border-white/10 overflow-hidden">
          <div className="flex border-b border-white/10">
            <button
              onClick={() => setActiveTab('deposit')}
              className={`flex-1 py-3 sm:py-4 px-4 sm:px-6 font-semibold transition-all text-sm sm:text-base ${
                activeTab === 'deposit'
                  ? 'bg-white/10 text-white'
                  : 'text-gray-400 hover:text-white hover:bg-white/5'
              }`}
            >
              <TrendingUp className="w-4 h-4 sm:w-5 sm:h-5 inline mr-2" />
              Deposit
            </button>
            <button
              onClick={() => setActiveTab('withdraw')}
              className={`flex-1 py-3 sm:py-4 px-4 sm:px-6 font-semibold transition-all text-sm sm:text-base ${
                activeTab === 'withdraw'
                  ? 'bg-white/10 text-white'
                  : 'text-gray-400 hover:text-white hover:bg-white/5'
              }`}
            >
              <Wallet className="w-4 h-4 sm:w-5 sm:h-5 inline mr-2" />
              Withdraw
            </button>
          </div>

          <div className="p-4 sm:p-6">
            {activeTab === 'deposit' ? (
              <div className="space-y-4 sm:space-y-6">
                <div>
                  <label className="block text-gray-400 text-sm mb-2">Amount (USDC)</label>
                  <input
                    type="number"
                    value={usdcAmount}
                    onChange={(e) => setUsdcAmount(e.target.value)}
                    placeholder="Enter USDC amount (min 10)"
                    className="w-full bg-white/5 border border-white/20 rounded-xl px-4 py-3 text-white placeholder-gray-500 focus:outline-none focus:border-blue-500 transition-all text-sm sm:text-base"
                  />
                  <p className="text-xs text-gray-500 mt-2">
                    Your USDC will be split equally (20% each) across 5 L1 tokens
                  </p>
                </div>

                {usdcAmount && parseFloat(usdcAmount) >= 10 && (
                  <div className="space-y-2">
                    <p className="text-gray-400 text-sm">Distribution Preview:</p>
                    {calculateSplit().map((item, idx) => (
                      <div key={idx} className={`bg-gradient-to-r ${item.color} bg-opacity-20 rounded-lg p-3 flex justify-between items-center`}>
                        <span className="text-white font-medium text-sm">{item.name} ({item.symbol})</span>
                        <span className="text-white/80 text-sm">${item.amount.toFixed(2)} USDC</span>
                      </div>
                    ))}
                  </div>
                )}

                {!isApproved ? (
                  <button
                    onClick={approveUSDC}
                    disabled={loading}
                    className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 sm:py-4 px-6 rounded-xl transition-all disabled:opacity-50 disabled:cursor-not-allowed text-sm sm:text-base active:scale-95"
                  >
                    {loading ? 'Approving...' : 'Approve USDC'}
                  </button>
                ) : (
                  <button
                    onClick={handleDeposit}
                    disabled={loading || !usdcAmount || parseFloat(usdcAmount) < 10}
                    className="w-full bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 hover:opacity-90 text-white font-semibold py-3 sm:py-4 px-6 rounded-xl transition-all disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2 text-sm sm:text-base active:scale-95"
                  >
                    {loading ? 'Processing...' : 'Deposit & Diversify'}
                    {!loading && <ArrowRight className="w-4 h-4 sm:w-5 sm:h-5" />}
                  </button>
                )}
              </div>
            ) : (
              <div className="space-y-4 sm:space-y-6">
                <div className="space-y-2 sm:space-y-3">
                  {ASSETS.map((asset, idx) => (
                    <div key={idx} className="bg-white/5 rounded-xl p-3 sm:p-4 flex justify-between items-center border border-white/5">
                      <div className="flex items-center gap-3">
                        <div className={`w-8 h-8 rounded-full bg-gradient-to-r ${asset.color} flex items-center justify-center text-xs font-bold text-white`}>
                          {asset.symbol.slice(0, 2)}
                        </div>
                        <div>
                          <div className="text-white font-semibold text-sm sm:text-base">{asset.symbol}</div>
                          <div className="text-gray-500 text-xs">{asset.name}</div>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="text-white font-semibold text-sm sm:text-base">
                          {formatBalance(balances[idx], asset.decimals)}
                        </div>
                        <div className="text-gray-500 text-xs">{asset.symbol}</div>
                      </div>
                    </div>
                  ))}
                </div>

                <div className="bg-yellow-500/10 border border-yellow-500/30 rounded-xl p-3 sm:p-4">
                  <p className="text-yellow-200 text-xs sm:text-sm">
                    ⚠️ Withdrawal will swap all tokens back to USDC. Market conditions and DEX fees may affect the final amount. Small balances may fail due to minimum swap requirements.
                  </p>
                </div>

                <button
                  onClick={handleWithdraw}
                  disabled={loading || balances.every(b => b === 0)}
                  className="w-full bg-gradient-to-r from-pink-500 via-purple-500 to-blue-500 hover:opacity-90 text-white font-semibold py-3 sm:py-4 px-6 rounded-xl transition-all disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2 text-sm sm:text-base active:scale-95"
                >
                  {loading ? 'Processing...' : 'Withdraw All to USDC'}
                  {!loading && <ArrowRight className="w-4 h-4 sm:w-5 sm:h-5" />}
                </button>
              </div>
            )}
          </div>
        </div>

        <div className="mt-4 sm:mt-6 text-center text-xs text-gray-500">
          Powered by Aerodrome on Base • Smart Contract is Immutable
        </div>
      </div>
    </div>
  );
}

export default App;
